    def killall(self, what: str, wait: Optional[int] = None, sig: Optional[int] = None, but: Optional[List[str]] = None) -> None:
        # logg.info("killall %s (but %s)", what, but)
        killed = 0
        if True:
            for nextpid in os.listdir("/proc"):
                try: pid = int(nextpid)
                except: continue
                cmdline = F"/proc/{pid}/cmdline"
                try:
                    cmd = open(cmdline).read().replace("\0", " ")
                    if fnmatch(cmd, what):
                        found = [name for name in (but or []) if name in cmd]
                        if found: continue
                        logg.info(" %s", F"kill {pid} # {cmd}")
                        os.kill(pid, sig or signal.SIGINT)
                        killed += 1
                except IOError as e:
                    if e.errno != errno.ENOENT:
                        logg.info(" killing %s", e)
                except Exception as e:
                    logg.info(" killing %s", e)
        for checking in range(int(wait or KILLWAIT)):
            remaining = 0
            for nextpid in os.listdir("/proc"):
                try: pid = int(nextpid)
                except: continue
                cmdline = F"/proc/{pid}/cmdline"
                try:
                    cmd = open(cmdline).read().replace("\0", " ")
                    if fnmatch(cmd, what):
                        found = [name for name in (but or []) if name in cmd]
                        if found: continue
                        remaining += 1
                except IOError as e:
                    if e.errno != errno.ENOENT:
                        logg.info(" killing %s", e)
                except Exception as e:
                    logg.info(" killing %s", e)
            if not remaining:
                return
            if checking % 2 == 0:
                logg.info("[%02is] remaining %s", checking, remaining)
            time.sleep(1)
        if True:
            for nextpid in os.listdir("/proc"):
                try: pid = int(nextpid)
                except: continue
                cmdline = F"/proc/{pid}/cmdline"
                try:
                    cmd = open(cmdline).read().replace("\0", " ")
                    if fnmatch(cmd, what):
                        found = [name for name in (but or []) if name in cmd]
                        if found: continue
                        logg.info(" %s", F"kill {pid} # {cmd}")
                        os.kill(pid, sig or signal.SIGKILL)
                        killed += 1
                except IOError as e:
                    if e.errno != errno.ENOENT:
                        logg.info(" killing %s", e)
                except Exception as e:
                    logg.info(" killing %s", e)
